// Smooth HSV to RGB conversion 
vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
	return c.z * mix( vec3(1.0), rgb, c.y);
}

 float over_than=0.1999;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;

    float clr =1.0*uv.y-(iTime/2.0);
    vec3 tx=texture(iChannel0,uv).rgb; 
    vec3 hsvColor = hsv2rgb_smooth(vec3(clr, 1.0, 1.0));

    over_than=max(0.1999,0.1999+sin(iTime/10.0));

    // ignore certain colors ! alien  cond ! if xx ? then zz  : then ww
     (tx.r>over_than)  ?  fragColor = vec4(hsvColor, 1.0) : fragColor=vec4(tx,1.0);
   // avec if !!!
    if(tx.g>over_than) fragColor = vec4(hsvColor, 1.0);else fragColor=vec4(tx,1.0);
    if(tx.b>over_than) fragColor = vec4(hsvColor, 1.0);else fragColor=vec4(tx,1.0);




}
