void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 coords = fragCoord.xy / iResolution.xy;
    float x = 1.0 - coords.x;
    float y = 1.0 - coords.y;

    vec4 texture = texture(iChannel0, vec2(x, y));
    texture.g = atan(sin(fract(y / 0.5 * sin(iTime))), 
                     sin(cos(x / 0.5 * y)));

	fragColor = texture;
}
