float hueToRgb(float p, float q, float t) {
    if (t < 0.0) {
        t += 1.0;
    }
    if (t > 1.0) {
        t -= 1.0;
    }
    if (t < 1.0 / 6.0) {
        return p + (q - p) * 6.0 * t;
    }
    if (t < 1.0 / 2.0) {
        return q;
    }
    if (t < 2.0 / 3.0) {
        return p + (q - p) * (2.0 / 3.0 - t) * 6.0;
    }
    return p;
}

vec3 hslToRgb(vec3 hsl) {
    float h = hsl.x;
    float s = hsl.y;
    float l = hsl.z;
    float q = l < 0.5 ? l * (1.0 + s) : l + s - l * s;
    float p = 2.0 * l - q;
    float r = hueToRgb(p, q, h + 1.0 / 3.0);
    float g = hueToRgb(p, q, h);
    float b = hueToRgb(p, q, h - 1.0 / 3.0);
    return vec3(r, g, b);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    float f = 0.0;
    for (float i = -0.005; i < 0.005; i += 0.0005) {
        f += texture(iChannel0, vec2(uv.x + i, 0.0)).r / 20.0;
    }
    float s = texture(iChannel0, vec2(uv.x, 1)).r;
    float m = clamp(f - uv.y, 0.0, 0.025) * 40.0;
    fragColor = vec4(hslToRgb(vec3(cos(uv.y + 0.4) * 0.3, 1, 0.5)).xy * m, uv.y - s, 1.0);
}
