//Assignment 3 CIS 566
//Adapted from https://www.shadertoy.com/view/MllXDH
//Adjust the BALLNUM to change the number metaballs
//Adjust the BALLRADIUS and BALLRADIUS2 to change the size of the metaballs
//Adjust the RADIUS to change the radius of the circle metaballs lie along

#define BALLNUM 8.0
#define BALLRADIUS 0.006
#define BALLRADIUS2 0.018
#define RADIUS 0.4

// bases on Cell Merge (prototype) from https://www.shadertoy.com/view/llsXD8
// with metaball
float mBall(vec2 uv, vec2 pos, float radius)
{
	return radius/dot(uv-pos,uv-pos);
}
#define PI 3.14159265
void mainImage( out vec4 f, in vec2 g )
{
    vec3 color_inner = vec3(1.0,1.0,1.0);

    vec2 s = iResolution.xy;
    vec2 uv = (2.*g-s)/s.y;

    float mb = 0.;

    float speed = 2.0;

    for(float i = 0.0; i<BALLNUM; i += 1.0)
    {
        float angle = 2.0 / BALLNUM * PI * float(i);
        float change = sin(iTime*speed + angle);
        float time = change * RADIUS;
        float finalradius = (-change * 0.5 + 0.5) * (BALLRADIUS2 - BALLRADIUS) + BALLRADIUS;
        mb += mBall(uv, vec2(RADIUS * sin(angle) + time * sin(angle), RADIUS * cos(angle) + time * cos(angle)), finalradius);
    }
    float stick = 0.6;
    float smooths = 0.01;
    vec3 mbin = vec3(255,253,255)/255.0 - color_inner * (1.-smoothstep(mb, mb+smooths, stick)); 
    f.rgb = vec3(mbin);
}
