float Pi = 3.14159265359;

float HERZ = 0.3;
float SPEED = 6.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 b = texture(iChannel0, uv);


    float l = length( uv - vec2( 0.5, 0.5 ) );
    vec3 light = vec3( 0.0, 0.0, 0.0 );
    if ( l * 2.0 < iTime )
    {
        light = mix(
            light,
            vec3( 0.28, 0.28, 0.28 ),
            max( sin( l * Pi * 2.0 / HERZ - iTime * SPEED ), 0.0 )
        );
    }

    fragColor = vec4( b.rgb + light, 1.0 );
}
