void mainImage( out vec4 fragColor, in vec2 fragCoord ) {


    vec2 uv = fragCoord.xy / iResolution.xy;
	float fftx  = texture( iChannel0, vec2(uv.x,1.25) ).x; 
	float wavex = texture( iChannel0, vec2(uv.x,2.75) ).x;
    float ffty = texture( iChannel0, vec2(uv.y,1.25) ).y;
    float wavey = texture( iChannel0, vec2(uv.x,2.75) ).y;

    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    // ring
    float u;
    if(int(iTime) % 30 < 10) {
        u = abs(sin((atan(p.y, p.x) - iTime * 0.5 + length(p) ) * 5.0)*0.5) + 0.25;
    } else {
        u = abs(sin((atan(p.y, p.x) + iTime * 0.5 + length(p) ) * 5.0)*0.5) + 0.25;
    }

    float t = 0.2 / abs(.535*sin(iTime) + u - length(fftx));  
    //fragColor = vec4(vec2(t*0.175)*vec2(fftx, ffty),wave*1.5,wave*0.5);
    fragColor = vec4(vec2(fftx*t, wavex+wavey),ffty*1.5,1.0);
}
