// variant of https://shadertoy.com/view/lscfDr

#define C      max(max(a.x,a.y),a.z)
#define R(x,y) t = max(t, max(abs(q.x+q.y),abs(q.x-q.y)) /1.41 )  

void mainImage(out vec4 O, vec2 U) {

    float t = iTime,v;
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );
    vec3  q = iResolution,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),    // ray direction
          p = 30./q, a;                       // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p,
        q.xz *= R, q.yz *= R,                 // rotation
        a = abs(q), v = C - 5.,               // cube
        q = mod(q,10.)-5.,
        t-=t, R(x,y), R(y,z), R(x,z) -6.,     // - 8 octaedrons
        v = max(v,.1-abs(mod(C,.5))),         // - slots  
     // v = max(v,.1-abs(C-4.)),            // - 3 slots           
     // v = max(v,.1-abs(C-3.5)),
     // v = max(v,.1-abs(C-3.)),
        v = max(v, .85-max(a.x,a.y)),
        v = max(v, .85-max(a.y,a.z)),
        v = max(v, .85-max(a.x,a.z)),
        t = max(v,-t),
        p += t*D;                             // step forward = dist to obj
}
