uint rngstate = uint(0);

//#define BLACK_AND_WHITE

uint randInt() {
    rngstate ^= rngstate << 13;
    rngstate ^= rngstate >> 17;
	rngstate ^= rngstate << 5;
    return rngstate;
}

void setSeed(uint seed) {
    rngstate = seed;
    randInt(); randInt(); randInt(); // Shift some bits around
}

float randFloat() {
    return fract(float(randInt()) / float(1<<32 - 5));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    setSeed(uint(fragCoord.y * iResolution.x + fragCoord.x));
    setSeed(randInt() + uint(floor(iTime*10000.0)));

    // Time varying pixel color

    #ifdef BLACK_AND_WHITE
    	vec3 col = vec3(randFloat());
    #else
    	vec3 col = vec3(randFloat(), randFloat(), randFloat());
    #endif


    fragColor = vec4(col,1.0);
}
