// N-Ary Sub-Division Pattern - written 2015 by Jakob Thomsen

void mainImage( out vec4 o, vec2 U )
{
    o -= o;
    U /= iResolution.xy;
    //float n = 2.;
    float n = mod(ceil(iTime), 9.) + 2.;
    //o += floor(n * fract(pow(n, ceil(U.y * 5.)) * U.x / n)) / (n - 1.);
    o += floor(mod(pow(n, ceil(U.y * 5.)) * U.x, n)) / --n; // FabriceNeyret2's optimization
}
