float wait = 0.1;
float timescale = 0.1;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy) / iResolution.y;
    float px = 1.0 / iResolution.y;

    float t = iTime * timescale;
    if (t <= wait) {
       fragColor = vec4(0.0); 
       return;
    }

    t -= wait;

    float d = distance(uv, vec2(t, 0.5 + sin(t / timescale) * 0.25));

    if (d < px) {
		fragColor = vec4(1.0);
    } else {
        discard;
    }

}
