// variant of https://shadertoy.com/view/XdyBWd

#define K 4.
#define exp(x) exp(min(30.,x))
#define smin(a,b) -log( exp( -K*(a) ) + exp( -K*(b) ) ) / K
#define smax(a,b) -(smin(-(a),-(b))) 

void mainImage(out vec4 O, vec2 U) {

    float t = iTime, z,i,l, r, h=6.;             // r: radius h: height/2.
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );
    vec3  q = iResolution,
          D = normalize(vec3(.3*(U+U-q.xy)/q.y, -1)),// ray direction
          p = 30./q, q0;                         // marching point along ray

    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 ) {
        q = p,
        q.xz *= R, q.yz *= R,                    // rotation
        i = 3. + floor(abs(q.x/6.))*2. + float(q.y>0.); // column id
        q0 = q; z = q.z;
        q.x = mod(q.x,6.)-3.,                    // repeat in x
        q.y = abs(q.y)-5.;                       // 2 rows
        q.z = mod(q.z,3.) - 1.5,                 // vertical blocks
        r = 1.+.2*max( sin(i*atan(q.y,q.x)),     // twisted radius
                       sin(i*z) );

        l = length(q.xy);
        t = l -r;                                // smooth square column radius decreasing with z
        if (q0.y>0.) t = smax(t,abs(q.z)-1.5);   // blocks top
        t = max(t,abs(z) -h),                    // inter range of Z

        p += t*D;                                // step forward = dist to obj
    }
    if (iMouse.z>0.) O.r = 1.-(l-.8)/.4;
}
