// original by Nikos Papadopoulos, 4rknova / 2013
// adjust by guowei / 2018
// WTFPL

#define A .0 // Amplitude
#define V 8. // Velocity
#define W 3. // Wavelength
#define T .01 // Thickness
#define S 2. // Sharpness
#define GAP vec2(0.02, 0.02) //gap for edge

float sine(vec2 p)
{
    return pow(T / abs((p.y + 0.0)), S);
}

//to detect if the pixels is in edge gap
bool detectEdge(vec2 fCoord, vec2 gap) {
    vec2 uv = fCoord.xy / iResolution.xy;
    vec2 edgeDistance = 0.5 -  abs(uv - 0.5);

    bvec2 edgeCompare = lessThan(edgeDistance, gap);
    bool isEdge = edgeCompare.x || edgeCompare.y;

    return isEdge;
}

//blur edge, p is detected as edge already
float blurEdge(vec2 fCoord, vec2 gap) {
    vec2 uv = fCoord.xy / iResolution.xy;

    if(uv.y > (1.0 - gap.y)) {
    	float blurValue = 1.0 - pow(T / abs(uv.y - (1.0 - gap.y)), S);
        return blurValue;
    } 

    if(uv.y < gap.y) {
    	float blurValue = 1.0 - pow(T / abs(uv.y - gap.y), S);
        return blurValue;
    }

    if(uv.x > (1.0 - gap.x)) {
    	float blurValue = 1.0 - pow(T / abs(uv.x - (1.0 - gap.x)), S);
        return blurValue;
    } 

    if(uv.x < gap.x) {
    	float blurValue = 1.0 - pow(T / abs(uv.x - gap.x), S);
        return blurValue;
    } 

    return 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(detectEdge(fragCoord, GAP)) {
        float channelColor = blurEdge(fragCoord, GAP);
    	fragColor = vec4(channelColor, 0.0, 0.0, channelColor);
    } else {
    	fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
