////////////////////////////////////
// 
//	beat that the sun is tuned to 
//	starts at around 30sec in
//  soundcloud link wont start were
//	it is supposed to 
//
////////////////////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv*2.0-1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec2 p = uv-vec2(-.8,.3);
    float q = .7;
    float height = .7;
    //vec3 color = vec3(.2,.175  ,.35);
    // alternate color
    vec3 color = vec3(.3,.275  ,.20);

    color -= .06*texture(iChannel1,uv).xyz;
    //float radius = .01+(.005*abs(cos(iTime*.1)));
    float sound = smoothstep(0.5,.65,texture(iChannel0,vec2(.3,.1)).x);
    //float bass = smoothstep(0.0,.9,texture(iChannel0,vec2(.6,.05)).y);
    float bass = smoothstep(0.3,.7,0.7*texture(iChannel0,vec2(.2,.7)).x);
    float radius = .01+.02*bass+(.005*abs(cos(iTime*.1)));
    float glow = .1+(.5*length(p*uv.y));//length(uv.y-sound);
    //float radius = .2+.1*cos(atan(((p.x)/(p.y)))*(11.0*sin(iTime)));
    float speed = 0.0;//sound ;

    color.rgb /= smoothstep(-.2,1.5+.5*sound+smoothstep(.0,1.0,abs(.5*sin(.2*iTime))),length(p));
    color.gb *= smoothstep(0.0,radius+glow,length(uv.y+height+q*.1*sin(uv.x+.8*speed+.8*iTime)));
    color.gb /= smoothstep(radius,radius+glow,length(uv.y+height+q*.1*sin(uv.x+.8*speed+.8*iTime)));

    color.gb *= smoothstep(0.0,radius+glow,length(uv.y+height+q*.15*sin(uv.x+.2*iTime)));
    color.gb /= smoothstep(radius,radius+glow,length(uv.y+height+q*.15*sin(uv.x+.2*iTime)));

    //color.y /= smoothstep(radius-.1,radius+.15,length(uv.y+.3+q*.2*3.1415*sin(uv.x+iTime)/3.0));

    fragColor = vec4(color,1.0);
}
