//effet algue
float amp = 0.1; //amplitude
float speed = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.y = 1.0-uv.y;

    //multiplier par 1-y permet de figer la base de l'image
	float xpos = uv.x+(1.0-uv.y)*amp*sin(uv.y+iTime*speed);

    vec4 t = texture(iChannel0, vec2(xpos, uv.y));


    fragColor = t;
}
