void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy) - vec2(.5,.5);

    float c = .5;
    float t = iTime+156.; // + 166.12 156;

    for(float f = 2.; f > .0; f -= .5)
    {
    	c /= sin( ( (t*.001*fract(t*.3))/length(uv)*5.5)*2.+1.*tan( uv.x*14. +t +f*f)/f*.2) / length(f*f*f*uv+uv) / 2.*atan(uv.x,uv.y);
    	c -= 1./fract(t-length(uv)*20.);
    }

    uv += vec2(sin(t),cos(t))*.2;

    // laaazy dupe
    for(float f = 2.; f > .0; f -= .5)
    {
    	c /= sin( ( (t*.001*fract(t*.3))/length(uv)*5.5)*2.+1.*tan( uv.x*14. +t +f*f)/f*.2) / length(f*f*f*uv+uv) / 2.*atan(uv.x,uv.y);
    	c -= 1./fract(t-length(uv)*20.);
    }

    float b = sin(t+uv.y*2.+mod(uv.x+t*.1,.2)*4.)*.35;

    // saw-like stencil: c^4 brings the noise
    if (uv.x-b < -.2 || uv.x > .2+b)
        c /= mod(1./c,.2+uv.y*.5-length(uv)*.2);

    if (uv.x-b-mod(b,.1) < -.2 || uv.x+b+mod(b,.1) > .2)
        c = 4./c;

    fragColor = vec4(c,c,c, 1.);
}
