void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r = fragCoord.xy / iResolution.xy;
    r.y = 1.0 - r.y;

    vec4 tex = texture(iChannel0,r.xy);

    float val = sin(r.y+r.x + iTime*3.) / 2.;
	float condition = step(0.0,val);

    tex += (vec4(val) * condition);

    fragColor = tex;
}
