void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 value =  texture(iChannel0, uv);        

    fragColor = texture(iChannel0, uv);
    //return;
  	vec4 col = texture(iChannel1,uv + value.gb * value.x*.5);
	fragColor = col;
}

//Mouse Radius
#define MR .035
//Mouse Force
#define MF .5

#define DT 1.
vec4 pic(sampler2D c,vec2 p) {
    if (p.x < 0. || p.y <0. || p.x > 1. || p.y > 1.)
        return vec4(0.);
    return texture(c,p);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixelSize = DT / iResolution.xy;  
    vec2 uv = fragCoord.xy / iResolution.xy;


    float d = 0.; 
    float dh = 0.;
    float dv = 0.;
    dh += pic(iChannel0, uv + vec2(pixelSize.x, 0)).x;
    dh += pic(iChannel0, uv - vec2(pixelSize.x, 0)).x;
    dv += pic(iChannel0, uv + vec2(0, pixelSize.x)).x;
    dv += pic(iChannel0, uv - vec2(0, pixelSize.x)).x;

    vec4 prev = pic(iChannel0,uv) * .999;
    d = prev.x;


    d = ((dh + dv)/2. + d) /3.;
    vec2 mCoord = texture(iChannel1,vec2(.5,.5)).xy;

    float f= step(distance(fragCoord/iResolution.xy , mCoord),MR);
    fragColor =vec4(
        max(d,f *MF),
    	mix(prev.g,(cos(iTime) * .5 + .5), f),
        mix(prev.b,(sin(iTime) * .5 + .5) , f),
        0.);

}

#define DAMPING .15
#define DT 1./60.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 prevState = texture(iChannel0,vec2(.5,.5)).xy;

    vec2 target = vec2(
        texture(iChannel1,vec2(.25,.5)).x*2. - .5,
        texture(iChannel1,vec2(.95,.5)).x*2. - .5
    );

    vec2 damped = prevState + (target - prevState) / DAMPING * DT;
    fragColor = vec4(damped,1.0,1.0);
}
