#define START_HUE .5
#define MIDDLE_HUE .0
#define END_HUE 1.

// Calucalate the luminance of a given pixel.
float luminance(vec3 rgb)
{
    return dot(rgb, vec3(0.3, 0.59, 0.11));
}

// Remap number to [0, 1]
#define to01(value, low, high) (value - low) / (high - low)

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 hsv = rgb2hsv(texture(iChannel0, uv).rgb);
    float hue = hsv.x;

    // Time varying pixel color
    vec3 col;
    if (hue < 0.5) {
    	hue = mix(START_HUE, MIDDLE_HUE, to01(hue, 0., 0.5));
    } else {
        hue = mix(MIDDLE_HUE, END_HUE, to01(hue, .5, 1.));
    }

    hsv.x = hue;

    // Output to screen
    fragColor = vec4(hsv2rgb(hsv), 1.0);
}
