// variant of https://shadertoy.com/view/4dVfWD

void mainImage( out vec4 O, vec2 U )
{
    U /= 32.;
    int i = ( iFrame + int(U.x) + int(U.y) ) %3;
    // O[i] = .11/vec4(.3,.59,.11,1)[i]; // O = sqrt(O);

    O =   i==0 ? vec4(1,0,0,0)+.5   // tuned so that gimp greyscale version
        : i==1 ? vec4(0,1,0,0)*.85  // is homogenous on my screen
        :        vec4(0,0,1,0)+.58;
}
