void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.xy;
  //uv.x -= 0.3;
  uv.x = 1.-uv.x;
  vec4 c0 = texture(iChannel0,uv);
  //uv.x += 0.3;
  uv -= 0.5*vec2(c0);
  vec4 c1 = texture(iChannel1,uv*1.);

  float mn = min(min(c0.x,c0.y),c0.z);
  float mx = max(max(c0.x,c0.y),c0.z);
  //float
  c0 -= vec4(vec3(mn-0.5),1.);

  //uv.y += (t0.y-0.5)*0.25;
  //uv.x += (t2.y-0.5)*0.5;
  fragColor = c1*c0;
}
