//useful function from iq
float expStep( float x, float k, float n )
{
    return exp( -k*pow(x,n) );
}

float f(float x){
return 1.0/(10.0*x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 q = vec2(1.0,1.0)/iResolution.xy;
    vec2 center = vec2(0.5, 0.5);

    float r = 0.0, g = 0.0, b = 0.0;


    b = 1.0/(length(uv-center+0.5*vec2(cos(iTime), sin(iTime)))*10.0);
    g = 1.2*b*b;

        r = 1.2*b*b;
    b += 1.5*expStep(0.2*sin(iTime*3.0)+length(0.1*sin(iTime*2.5)+uv-center+0.5*vec2(sin(iTime*3.0), cos(iTime*3.0)))*10.0, 3.0+0.7*sin(iTime*50.0), 2.0);
    g += 0.8*b;
    r += 0.8*b;
	fragColor = vec4(r, g, b,1.0);
}
