int GCD(int a, int b)
{
    for (int i = 0; i < 32; i++)
    {
        if (b != 0)
        {
            int t = b;
            b = a % b;
            a = t;
        }
        else break;
    }
    return a;
}

void mainImage(out vec4 c, vec2 f)
{
    ivec2 v = ivec2(f * 0.3);
    int isCoprime = 2 - GCD(v.x, v.y);
    c = vec4(isCoprime);
}
