vec2 getChar(vec2 uv,  int c)
{     
    float x = (1.0 / 16.) *  fract ( float(c) / 16.)  * 16. + uv.x /16. ;   
    float y = (1.0/16.) *  (16.- float(c/16) - 1.0 ) + uv.y/16. ;     
    return vec2(x, y);
}
#define myThoughtVER

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    int char = 49; // 48->0  49->1 72->H  and so on

	vec2 result = getChar(uv, char); 

    #ifdef oldVER
		fragColor = texture( iChannel0, result).xxxx;
    #endif


     #ifdef iqVer// suggest by iq
    	float d = texture( iChannel0, result).w-0.5;
     	float f = 1.0-smoothstep(0.0,fwidth(d),d);
     	fragColor = vec4(f,f,f,1.0);
    #endif


    #ifdef  paulhouxVER
        float d=  texture( iChannel0, result).w ; 
        float w = fwidth(d);
        float a = smoothstep( 0.5 - w, 0.5 + w, d );
        fragColor = vec4(a, a, a, 1.0);
    #endif


    #ifdef  myThoughtVER
        float d=  texture( iChannel0, result).w ; 
        float a = step(.5, d);
        fragColor = vec4(a, a, a, 1.0);
    #endif
}







