void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((50.0 + 0. * iTime * 0.5) / 3.0) * ((fragCoord.xy - (iResolution.xy / 2.0)) / iResolution.y);

    float value = 2. * iTime + (1.*iTime * sign(uv.y * uv.x) + uv.x/2. * sign(uv.y) + (uv.y/2.) * sign(uv.x)) * sin(uv.x) * sin(uv.y);
    float color = sin(value) * 3.0;
    //color = color * sign(uv.x) * sign(uv.y); // revision 2


    float low = abs(color);
    float med = abs(color) - 1.0;
    float medhigh = abs(color) - 1.5;
    float high = abs(color) - 2.0;

    vec4 redColor = vec4(high, high, med,1.0);
    vec4 blueColor = vec4(med, high, high,1.0);

	  //fragColor = vec4(med, high, high,1.0);
      //fragColor = vec4(high, high, med,1.0);
    if(color > 0.) {
      //fragColor = vec4(high, high, med,1.0);
      redColor = vec4(high, high, med,1.0);
      blueColor = vec4(med, high, high,1.0);
    } else {
      blueColor = vec4(med, medhigh, high,1.0);
      redColor = vec4(med, high, high,1.0);
    }

    fragColor = redColor;

    float r = iTime / 5.;
    vec2 rv = uv.xy * mat2(cos(r), sin(r), -sin(r), cos(r));
    float angle = 2. * abs(atan(rv.y, abs(rv.x)) / 3.14159);

    fragColor = redColor * 0.8;// * (angle);// + blueColor * (1. - angle);

    //fragColor = vec4(angle, angle, angle, 1.0);

}
