void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = 50.0 * ((fragCoord.xy - iResolution.xy/2.)/ iResolution.y) + vec2(0, 0);

    float d = 1.0;// + sqrt(length(uv)) / 109.0;
    float t = 0. + iTime;
    float value = d * t + (t * 0.5) * cos(uv.x) * cos(uv.y);
    float color = sin(value) * 3.0;
    //color = color * sign(uv.x) * sign(uv.y); // revision 2


    float low = abs(color);
    float med = abs(color) - 1.0;
    float medhigh = abs(color) - 1.5;
    float high = abs(color) - 2.0;

    vec4 metalColor;
    vec4 lifeColor;

    if(color > 0.) {
      metalColor = vec4(med, medhigh, high,1.0);
      lifeColor = vec4(high, high, med,1.0);
    } else {
      metalColor = vec4(medhigh, medhigh, medhigh,1.0);
      lifeColor = vec4(med, high, high,1.0);
    }

    fragColor = lifeColor * 1.1;
}
