void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float origX = uv.x;
    uv.x += iTime / 100.0;
    uv.y *= -1.0;

	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    //float reflectionOffset = 0.2 - cos(origX * 3.14159 * 2.0) * 0.1;
    float reflectionOffset = texture(iChannel2, vec2(0.2, origX / 30.0)).r * 0.5 + 0.1;
    vec4 orig = texture(iChannel0, uv);
    float refY = (1.0 - reflectionOffset) - (reflectionOffset - (1.0 - uv.y));
    refY = refY + texture(iChannel1, uv).r * 0.1;

    // extra samples give us a horiz blur
    float pixel = 1.0 / iResolution.x;
    vec4 refl = (
        //texture(iChannel0, vec2(uv.x, refY)) + 
        texture(iChannel0, vec2(uv.x - pixel, refY)) + 
        texture(iChannel0, vec2(uv.x - pixel * 2.0, refY - pixel * 1.0)) + 
        texture(iChannel0, vec2(uv.x - pixel * 3.0, refY + pixel * 1.0)) + 
		//texture(iChannel0, vec2(uv.x + pixel, refY)) +
        texture(iChannel0, vec2(uv.x + pixel * 2.0, refY - pixel * 1.0)) +
        texture(iChannel0, vec2(uv.x + pixel * 3.0, refY + pixel * 1.0))
    ) / 5.0;
    //fragColor = texture(iChannel0, uv * vec2(1.0, -1.0));


    fragColor = mix(orig, 
                    mix(refl, vec4(0.0, 0.25, 0.5, 1.0), 0.6),
                    step(-reflectionOffset, uv.y));
}
