vec4 lerp4( in vec4 v1, in vec4 v2, in float t )
{
 	return v1*(1.0-t) + v2*t;   

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float x = abs(sin(iTime));
    float width = 0.05;

    if (uv.x > (x - width) && uv.x < (x + width))
    {
        if (uv.x < x)
        {
        	fragColor = lerp4(vec4(0,0,0,1), texture(iChannel0, uv), abs(x-uv.x)/width);
        }
        else
        {
            fragColor = lerp4(vec4(0,0,0,1), texture(iChannel1, uv), abs(x-uv.x)/width);
        }
    }
    else
    {
        if (uv.x < x)
        {
        	fragColor = texture(iChannel0, uv);
        }
        else
        {
            fragColor = texture(iChannel1, uv);
        }
    }
}
