void mainImage( out vec4 o,  vec2 u )
{
    float C,S, t=(iTime-11.)/1e3;
    o-=o;
    vec2 R = iResolution.xy, p;
	u = 6.3*(u+u-R)/R.y;

#define B(k) ceil( (p=cos(u*=mat2(C=cos(t),S=-sin(t),-S,C))).x * p.y )  * (.5+.5*cos(k))

    for (float a=0.; a<6.3; a+=.1)
        o += vec4(B(a),B(a+2.1),B(a-2.1),1) / 31.;
}


