void mainImage( out vec4 col, in vec2 p ) {
    p /= iResolution.xy;

    float t = iTime;

    col.rgb = vec3(texture(iChannel0, p).r, texture(iChannel0, p - 0.025 + cos(t + p.y + p.x)*0.1).g, texture(iChannel0, p + 0.04  + sin(t + p.y + p.x)*0.1).b);
	col.rgb *= 1.75;

    float a = t*0.25;

    col.gb -= texture(iChannel1, (p + length(p) ) * mat2(cos(a), -sin(a), 
                                                           sin(a), cos(a))).gb*0.15 + length(p - 0.5);

    col.rgb -= length(p - 0.5) * 0.75;
}
