void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);


    vec2 xy = fragCoord.xy / iResolution.xy; // Condensing this into one line
    vec2 pos = fragCoord.xy;

    float screenMiddleX = iResolution.xy.x / 2.0;
    float screenMiddleY = iResolution.xy.y / 2.0;

	vec4 texColor = texture(iChannel0,xy); // Get the pixel at xy from iChannel0

    if (pos.x < screenMiddleX) {
        if (pos.y < screenMiddleY) {
            //nothing
        } else {
            texColor = texture(iChannel1,xy);
        }
    } else {
        if (pos.y < screenMiddleY) {
            texColor = texture(iChannel2,xy);
        } else {
            texColor = texture(iChannel3,xy);
        }
    }

    fragColor = texColor;
}
