void mainImage( out vec4 color, in vec2 fragCoord )
{
  fragCoord /= iResolution.xy;
  vec2 q = vec2(fragCoord.x*2. -1. , 1. - fragCoord.y*2.);
  float d = length(q);
  color = vec4(0.);
  if(d<=1.)
  {
    float k = d*d;
    vec2 v = normalize(q) * k * .5;
    vec2 uv = vec2(0.5 + v.x , .5 - v.y);
    color = texture(iChannel0, uv);
  }  

}
