void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pixelP = fragCoord.xy / iResolution.xy;

    vec3 color = vec3( 1.0, 1.0, 1.0 );

    vec2 point = iMouse.xy / iResolution.xy;
    vec2 dist2Point = pixelP - point;
    float angle2Point = atan( dist2Point.y, dist2Point.x );
    float radius = 0.1 + 0.4*tan( angle2Point * iTime * iTime * iTime );

    color *= smoothstep( radius, radius + 0.01, length(dist2Point ) );

	fragColor = vec4( color ,1.0);
}
