//Otra variable global util es iTime, que contiene el tiempo. Con esto
//se puede hacer que el color resultante varíe seguna una función del tiempo

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}
