// get factor between 0. and 1. 
// increasing during first second 
// and decreasing during second second periodicaly
float getTimeWaveFactor () {
	return mod(iTime, 2.0) < 1. ? mod(iTime, 1.) : 1. - mod(iTime, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 positionFactor = fragCoord.xy / iResolution.xy;
    float timeWaveFactor = getTimeWaveFactor();
	float radius = timeWaveFactor * iTime;

	// distance between the mouse cursor and the current pixel
	float distanceMousePixel = distance(iMouse.xy, fragCoord.xy);

	// blue color factor
	float blue = (radius - distanceMousePixel) / radius;
	fragColor = vec4(
        0., 
    	0., 
    	blue, 
        1.
    );
}
