float stripes( float value, float thickness, float gutter ) {
    return step( gutter, mod( value, thickness + gutter  ) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float vertStripes = stripes( fragCoord.x - iTime * 20., 5., 5. );

    vec2 centerPt = vec2( iResolution * .5 );
    float radiusCircle = cos( iTime * 1.5 ) * iResolution.y * 0.10 + iResolution.y * 0.25;
    float circle = step( 1., distance ( centerPt, fragCoord ) / radiusCircle );

    vec3 outerCirlerStripes = vec3( vertStripes * circle );
    vec3 innerCirlerStripes = vec3( ( vertStripes * -1. + 1. ) * ( 1. - circle ) );
    vec3 coeffColor = outerCirlerStripes + innerCirlerStripes;

    vec3 c1 = vec3( 106., 45., 190. ) / 255.;
    vec3 c2 = vec3( 246., 231., 62. ) / 255.;

    vec3 color = mix( c1, c2, coeffColor );


    fragColor = vec4( color, 1.0 );
}
