void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 tex2 = texture(iChannel1, uv).rgb;

    //Make black and white
    tex2.rgb = vec3((tex2.r + tex2.g + tex2.b)/3.0);

    float offMax = 0.4;
    float offDir = iTime;
    uv.x+=cos(offDir) * mix(0.0,offMax,tex2.r);
    uv.y+=sin(offDir) * mix(0.0,offMax,tex2.r);

    vec3 tex1 = texture(iChannel0, uv).rgb;
    vec3 pixel = tex1.rgb;
	fragColor = vec4(pixel,0);
}
