void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float mini = 0.0;
    float mind = 0.0;
    float bstd = 1.0;

    for(float dst = 0.05; dst <= 1.0; dst += 0.1) {
        for(float d = 0.0; d < 3.14; d += 0.01) {
            float dif = -abs(distance(texture(iChannel0, (fragCoord + vec2(cos(d),sin(d))) / iResolution.xy ).rgb, 
                             texture(iChannel0, (fragCoord + vec2(cos(d + dst),sin(d + dst))) / iResolution.xy ).rgb));
            if(dif < mind) {
                mind = dif;
                mini = d;
                bstd = dst;
            }
        }
    }
    fragColor = texture(iChannel0, (fragCoord + vec2(cos(mini),sin(mini))) / iResolution.xy );
    mini += 1.57;
    fragColor += texture(iChannel0, (fragCoord + vec2(cos(mini + bstd),sin(mini + bstd))) / iResolution.xy );
    fragColor /= 2.;
    /*fragColor += texture(iChannel0, uv);
    fragColor /= 2.;*/
    //fragColor = texture(iChannel0, uv);
}
