#define TEX_WIDTH 256.0
#define FRAME_WIDTH 40.0
#define FRAME_HEIGHT 32.0

#define BEV_SIZE 3.0
#define NUM_FRAMES 6.0
#define FPS 12.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float offset = floor(mod(iTime / (1.0/FPS), NUM_FRAMES));

    float tX = BEV_SIZE * offset * (FRAME_WIDTH / TEX_WIDTH);
    vec2 texPos = vec2(tX + (fragCoord.x / TEX_WIDTH), fragCoord.y / FRAME_HEIGHT) / BEV_SIZE;

    vec2 modTexPos = mod(texPos, vec2(1.0));

    vec2 l = (mod(fragCoord, BEV_SIZE) / BEV_SIZE); 
    l = vec2(0.75) + (smoothstep((1.0/BEV_SIZE),(1.5 - 1.0/BEV_SIZE), l) * 0.55);

    if(fragCoord.x >= 0.0 && fragCoord.x < FRAME_WIDTH * BEV_SIZE &&
       fragCoord.y >= 0.0 && fragCoord.y < FRAME_HEIGHT * BEV_SIZE) {        
    	fragColor = texture(iChannel0, modTexPos) * l.x * l.y;
	}
}
