vec4 doCol( void )
{
    return vec4(texture(iChannel0, gl_FragCoord.xyz / iResolution.xyz).rgb / sin(gl_FragCoord.y / iResolution.y), 1.0);
}
vec4 sX( vec4 color, vec3 p )
{
    return vec4(color) - (length(p) - 1.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 bowlCol = doCol();
    float t = 0.0;
    vec4 sp = sX( bowlCol, vec3(0.0, 0.0, -3.0) + t * normalize(vec3(uv, 1.0)));
    fragColor = mix(sp/bowlCol, bowlCol, length(tan(tan(uv))));
}

