float Noise(vec2 uv){

float speed = 500.0;
float time = iTime / speed;

float waves;    

    uv = uv * 2.0;

waves = texture(iChannel0,(uv.yx* vec2(0.5,0.2) + vec2(0,uv.y)) - time).x * 2.0 - 1.0;


waves += texture(iChannel0,(uv* vec2(0.5,0.2) + vec2(0,uv.x)) - time).x * 2.0 - 1.0;


waves += texture(iChannel0,(uv.yx* vec2(0.5,0.2) + vec2(0.4,uv.x)) - time).x * 2.0 - 1.0;


waves += texture(iChannel0,(uv* vec2(0.5,0.2) + vec2(0.1,uv.x * 0.3)) - time).x * 2.0 - 1.0;


waves += texture(iChannel0,(uv.yx* vec2(0.5,0.2) + vec2(0.9,uv.y)) + time).x * 2.0 - 1.0;


waves += texture(iChannel0,(uv.yx* vec2(0.5,0.2) + vec2(1.0,uv.y * 1.3)) + time).x * 2.0 - 1.0;


waves += texture(iChannel0,(uv.yx* vec2(0.5,0.2) + vec2(2.4,uv.y * uv.x)) + time).x * 2.0 - 1.0;


waves += texture(iChannel0,(uv.yx* vec2(0.5,0.2) + vec2(0,uv.y * (uv.y + uv.x / 12.0 * (uv.x * 0.2)))) + time).x * 2.0 - 1.0;


waves += waves * waves;

waves = pow(waves,8.0);


return waves;

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	fragColor += Noise(uv);
}
