void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float dist=length(fragCoord-vec2(iResolution.x/2.,iResolution.y/2.))-100.;

    float grad = smoothstep(0.,1.,dist);

    vec3 col = (0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)))*grad+
        	   (0.5 + 0.5*sin(iTime+uv.xyx*4.+vec3(0,2,4)))*(1.-grad);

    // Output to screen
    fragColor = vec4(col,1.0);
}
