//color of the background (white)
vec4 bgCol = vec4(1.0,1.0,1.0,1.0);

//colors for the house, chimney and windows
vec4 houseCol = vec4(0.0,0.0,1.0,1.0);
vec4 chimneyCol = vec4(1.0,0.0,0.0,1.0);
vec4 windowCol = vec4(0.0,1.0,0.0,1.0);

//positions for the house, chimney and windows
vec2 housePos = vec2(0.8,0.3);
vec2 chimneyPos = vec2(0.9,0.55);
vec2 windowPos1 = vec2(0.7,0.25);
vec2 windowPos2 = vec2(0.9,0.25);

//width and height for the house, chimney and windows
vec2 houseSize = vec2(0.2,0.2);
vec2 chimneySize = vec2(0.04,0.05);
vec2 windowSize = vec2(0.06,0.06);

//ON and OFF toggles for the 3 layers
float houseOn = 1.0;
float chimneyOn = 1.0;
float windowsOn = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    //where should the house alpha be 1 or 0
    houseCol.a *= step(length(uv.x - housePos.x), houseSize.x) * 
        step(length(uv.y - housePos.y), houseSize.y) * houseOn;

    //where should the chimney alpha be 1 or 0
    chimneyCol.a *= step(length(uv.x - chimneyPos.x), chimneySize.x) * 
        step(length(uv.y - chimneyPos.y), chimneySize.y) * chimneyOn;

    //where should the windows' alpha be 1 or 0
    windowCol.a *= step(length(uv.x - windowPos1.x), windowSize.x) * 
        step(length(uv.y - windowPos1.y), windowSize.y) + 
        step(length(uv.x - windowPos2.x), windowSize.x) * 
        step(length(uv.y - windowPos2.y), windowSize.y) * windowsOn;

    //combines all three layers plus the background
    vec4 outColor = mix(bgCol, houseCol, houseCol.a);
    outColor = mix(outColor, chimneyCol, chimneyCol.a);
    outColor = mix(outColor, windowCol, windowCol.a);

    // Output to screen
    fragColor = outColor;
}
