#define PI 3.1415926535897932384626433832795



// Number of mirror segments

#define N 16.0


// Speed of rotation

#define S (PI/16.0)


// ZOOM

#define Z 1.5




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float fft1=1.0-texture(iChannel1,vec2(440.0/11025.0,0.25)).r; //maps float to frequency intensity of media in channel
    float fft2=2.0-texture(iChannel1,vec2(880.0/11025.0,0.25)).r; 
    float fft3=0.5*texture(iChannel1,vec2(200.0/11025.0,0.25)).r; 


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cc = uv - iMouse.xy/iResolution.xy;//vec2(0.5,0.5); //centre of mirrors
    vec2 po = vec2( atan(cc.x,cc.y) , length(cc) ); 

    float s=0.1+tan(fft3/32.0);//S; //rotation
    float n=3.0+floor(3.0+((iTime/(iTime*8.0))*fft2));//N; //number of mirrors - use floor(x) ceil(x) to round to whole number
    float z=(0.8+sin(1.0))*1.5+fft2*fft1-(1.0/tan(7.0));//1.0+cos(iTime*0.2);//Z; //zoom

	float f=(2.0*PI/n);

    float m=mod((2.0*PI)+po.x,f);

    if(m>f/2.0){ m=f-m; }

    po.x = m+(iTime*s);
    po.y /=z;

    cc= vec2(cos(po.x),sin(po.x))*po.y;


    // Output to screen
    fragColor = texture( iChannel0, cc+vec2(0.5,0.5) ).rgba;
    fragColor += 0.2*texture( iChannel2, cc+vec2(0.5,0.5) ).rgba; //superimpose texture on webcam chanel 1

   // fragColor=fragColor*4.0-2.0; // contrast dark
 //fragColor=vec4(1)-fragColor-0.5; // contrast lighter
     fragColor=fragColor*5.0-1.5; // contrast

}
