vec3 getTexure(vec2 coord){
	return texture(iChannel0,coord.st).rgb;    
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 texC = uv;

    vec2 lpos = iMouse.xy / iResolution.x;

    vec2 texC2 = fragCoord.xy / iResolution.x;

    texC = mix(texC2, (texC * 2.0 - lpos * 2.0) * 0.0 * 0.5 + lpos, (1.0 / (distance((texC2 * 2.0 - lpos * 2.0) * 10.0 * 0.5 + lpos, lpos) - 1.0))); //Black hole shader

	vec3 getColor = getTexure(texC);

    getColor *= clamp(pow(distance(texC2, vec2(lpos)),8.8) * 300000000.0,0.0,1.0);

	fragColor = vec4(getColor,1.0);
}
