float field(vec3 pos, float time) {
    float field = 0.0;
    float fieldbound = 100000.0;
    for(float i = -10.0; i < 10.0; i += 1.0) {
        vec3 c = vec3(i * 0.2, sin(i * 0.2 + time * 0.5), cos(i * 0.7 + time * 0.8));
        vec3 pc = pos - c;
        float r = dot(pc, pc);
    	field += (1.0 - step(0.5, r)) * (r * r - r + 0.25);

        fieldbound = min(fieldbound, sqrt(r) - 0.75);
    }
    field = 0.1 - field;

    return(max(field, fieldbound));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 coords = (2.0 * fragCoord.xy - iResolution.xy) / max(iResolution.x, iResolution.y);
	vec3 ray_dir = normalize(vec3(coords.x, coords.y, 1.0 + 0.0 * sqrt(coords.x * coords.x + coords.y * coords.y)));
	vec3 ray_pos = vec3(0.0, 0.0, -3.0);

	float i = 32.0;
	for(int j = 0;j < 32;j++) {
		float dist = field(ray_pos, iTime);
		ray_pos += dist * ray_dir;

		if(abs(dist) < 0.05) { i = float(j); break; }
	}

	float c = i / 32.0;
	fragColor = texture(iChannel0, vec2(c / 100.0 + 0.3, 0.0));
}
