info.description:
Started programming shaders this afternoon, heaps of fun!
Playing with angles, and exploring arbitrary numbers.

// Day #1 shader programming.

renderpass.code:
const float PI = 3.141592653589793238462643383;
const float PI2 = PI * 2.0;
const float pixelation = 20.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float ar = iResolution.x / iResolution.y;
    vec4 temp = vec4(0.0,0.0,0.0,1.0);
    vec2 center = vec2(0.5,0.5);
    vec2 fromCenter = uv - center;
    fromCenter.x *= ar;

    fromCenter *= pixelation;
    fromCenter = floor ( fromCenter );
    fromCenter /= pixelation;

    float angle = mod( atan( fromCenter.y, fromCenter.x) - iTime * 1.0 , PI2)  ;
    float fromCenterLength = length(fromCenter);

    temp.g = angle / PI2;
    temp.g -= sin(iTime * 1000.0 + fragCoord.x + fragCoord.y);

    fragColor = temp;
}
