void mainImage(out vec4 o, in vec2 q)
{  
    float s = 2.;//scale factor

    o = texture(iChannel0, fract((q / iResolution.xy-.5)/s+.5));
}

//This function was borrowed from
//https://www.shadertoy.com/view/ltB3zD
//Poltergeist Static Random Noise by dcerisano
#define r(p,s) fract(sin(dot(p*s, vec2(12.9898, 78.233)))*43758.5453)

void mainImage(out vec4 o, in vec2 q)
{
    o = vec4( r(q, 1.), r(q, 2.), r(q, 3.), 1); //colorfull version
    vec4 f = sign(mod(o/4.*1e2,2.)-1.); //rgb frequencies

    //ping-pong glow
    o = abs(mod(o+iTime*f, 2.)-1.);
}
