float eval(vec2 uv)
{
    float pr = 0.5;
	 float	xn = 1.1;
	 float	yn = 12.0;
	 float	zn = cos(uv.x*uv.y);

    //zooooom
    xn =(uv.x) *65.0;
    yn =(uv.y)*40.0;

    float intens = 0.0;
    //iterate at this pixel for a while and solve lorenz oscillator-ey equations
    for(int i = 0; i <20; i++)
    {
        xn = abs(log(xn*(0.1 + cos(iTime*0.2)*0.05)));
        zn = abs(log(zn*(0.1 + cos(iTime*0.2)*0.05)));

        float xnn = xn*zn + (pr * (yn - zn));
		float ynn = yn*zn + (-xn * cos(zn*zn));
		float znn = zn*xn + (xn * zn*pr);

		xn = xnn;
		yn = ynn;
		zn = znn;
        if(length(vec3(xnn,ynn,znn))>300.0)
        {
            intens = length(vec3(xnn,ynn,znn))/2000.0;
            break;
        }
    }

    return intens;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 unit = vec2(1.0)/iResolution.xy;

    float intens = eval(uv);

    vec3 samp = texture(iChannel0,vec2(intens)).xyz;

	fragColor = vec4(samp,1.0) * pow(intens,0.004);
}
