    float tetex = 0.5;
	float tetey = 0.5;
	float bpm = 120.0;
	float leftx = 0.5;
	float lefty = 0.5;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    lefty = 1.0 + 0.5 * sin(iTime);
    vec3 col = vec3(0.0);

    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;

    p.x *= iResolution.x/iResolution.y;

    if (p.x > 0.0) p.x = -p.x;
    if (p.y > 0.0) p.y = -p.y;

    float enno = tetex;
    float ramon = tetey;

    p += enno * smoothstep(enno - ramon, enno + ramon, p.x*p.x + p.y*p.y);

	float zoo = 0.001 + 0.0008 * sin(0.00001 * pow(bpm, 2.0) * iTime);

	vec2 cc = vec2(-0.52941,0.526141) + p*zoo;

	vec2 t2c = vec2(cos(iTime), sin(iTime));

    vec2 z  = vec2(0.0);
	float trap = 1000.0;

	const int iterations = int(100 + int(0.1) * 100 );

    for( int i=0; i < iterations; i++ )
    {
        if( dot(z,z)>1024.0 ) break;
        z = cc + vec2( z.x*z.x - z.y*z.y, 2.0*z.x*z.y ); // Z -> ZÂ² + c	
		trap = min( trap, dot(z-t2c,z-t2c) );
    }
    float etFactor = dot(z,z);
    float trapFactor = trap;

    col += mix(clamp(1.0 - etFactor, 0.0, 1.0), clamp(trapFactor, 0.0, 1.0), lefty);
	fragColor = vec4( col, 1.0 );
}
