const int MAXITER = 1024;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    float t = iTime;

    float zoom = cos(0.1*t)*0.4;
    zoom = pow( zoom,2.) + 0.00001;
    uv *= zoom;
    float x = -1.16484;
    float y = 0.25587;
    uv += vec2(x,y);

    vec2 z = uv;
    vec2 comp = vec2(-1.,0.28101011);
    vec3 c = vec3(0.0);

    for(int i=0; i < MAXITER; i++)
    {
        c = vec3(1./float(MAXITER) * float(i));
        if ((z.x*z.x + z.y*z.y) > 4.) break;
        z = vec2(z.x*z.x - z.y*z.y, 2.*z.y*z.x) + comp;
    }

	fragColor = vec4(c,1.0);
}
